package semana04;

/**
 * Classe para realizar operações matemáticas
 * 
 * @version 1.0
 * @since 1.0
 * 
 */
public class Calculos {
	/**
	 * Recebe dois valores inteiros e apresenta sua soma em tela
	 * 
	 * @param a - primeiro valor
	 * @param b - segundo valor
	 */
	public static void somar(int a, int b) {
		// TODO Auto-generated method stub

		System.out.println(a + b);

	}

	/**
	 * Recebe dois valores inteiros e apresenta sua soma em tela
	 * 
	 * @param a - primeiro valor
	 * @param b - segundo valor
	 * @param c - terceiro valor
	 */
	public static void somar(int a, int b, int c) {
		// TODO Auto-generated method stub

		System.out.println(a + b + c);

	}

	public static void somar(double a, double b, double c) {
		System.out.println(a + b + c);
	}

	public static void somar(double d, double e, int i, int j, int k) {
		// TODO Auto-generated method stub
		System.out.println(d + e + i + j + k);
	}

}
